and delete_flag=0

SELECT
dept_annual_bonus_id,annual_bonus_year,company_annual_bonus,department_annual_bonus,status,delete_flag,create_by,create_time,update_by,update_time
FROM dept_annual_bonus
WHERE delete_flag=0

                    and dept_annual_bonus_id=#{deptAnnualBonus.deptAnnualBonusId}


                    and annual_bonus_year=#{deptAnnualBonus.annualBonusYear}


                    and company_annual_bonus=#{deptAnnualBonus.companyAnnualBonus}


                    and department_annual_bonus=#{deptAnnualBonus.departmentAnnualBonus}


                    and status=#{deptAnnualBonus.status}


                    and delete_flag=#{deptAnnualBonus.deleteFlag}


                    and create_by=#{deptAnnualBonus.createBy}


                    and create_time=#{deptAnnualBonus.createTime}


                    and update_by=#{deptAnnualBonus.updateBy}


                    and update_time=#{deptAnnualBonus.updateTime}

mapper.update:
UPDATE dept_annual_bonus
SET     delete_flag= 1,
        update_by=#{deptAnnualBonus.updateBy},
        update_time=#{deptAnnualBonus.updateTime}
WHERE
        dept_annual_bonus_id=#{deptAnnualBonus.deptAnnualBonusId}

mapper.delete:
