mapper.select:
SELECT performance_appraisal_id, performance_rank_id, performance_rank_name, appraisal_year, appraisal_name, cycle_flag, cycle_type, cycle_number, appraisal_start_date, appraisal_end_date, filing_date, appraisal_flow, appraisal_object, self_defined_columns_flag, appraisal_status, delete_flag, create_by, create_time, update_by, update_time FROM performance_appraisal WHERE performance_appraisal_id in
#{item}
and delete_flag=0

SELECT
pa.performance_appraisal_id,
pa.performance_rank_id,
pa.performance_rank_name,
pa.appraisal_year,
pa.appraisal_name,
pa.cycle_flag,
pa.cycle_type,
pa.cycle_number,
pa.appraisal_start_date,
pa.appraisal_end_date,
pa.filing_date,
pa.appraisal_flow,
pa.appraisal_object,
pa.self_defined_columns_flag,
pa.appraisal_status,
pa.delete_flag,
pa.create_by,
pa.create_time,
pa.update_by,
pa.update_time
FROM performance_appraisal pa
WHERE pa.delete_flag=0

    and pa.performance_appraisal_id=#{performanceAppraisal.performanceAppraisalId}


    and pa.performance_rank_id=#{performanceAppraisal.performanceRankId}


    and pa.performance_rank_name like concat('%', #{performanceAppraisal.performanceRankName}, '%')


    and pa.appraisal_year=#{performanceAppraisal.appraisalYear}


    and pa.appraisal_name like concat('%', #{performanceAppraisal.appraisalName}, '%')


    and pa.cycle_flag=#{performanceAppraisal.cycleFlag}


    and pa.cycle_type=#{performanceAppraisal.cycleType}


    and pa.cycle_number=#{performanceAppraisal.cycleNumber}


    and pa.appraisal_start_date=#{performanceAppraisal.appraisalStartDate}


    and pa.appraisal_end_date=#{performanceAppraisal.appraisalEndDate}


    and pa.filing_date=#{performanceAppraisal.filingDate}


    and pa.appraisal_flow=#{performanceAppraisal.appraisalFlow}


    and pa.appraisal_object=#{performanceAppraisal.appraisalObject}


    and pa.self_defined_columns_flag=#{performanceAppraisal.selfDefinedColumnsFlag}


    and pa.appraisal_status=#{performanceAppraisal.appraisalStatus}


    and pa.delete_flag=#{performanceAppraisal.deleteFlag}


    and pa.create_by=#{performanceAppraisal.createBy}


    and pa.create_time=#{performanceAppraisal.createTime}


    and pa.update_by=#{performanceAppraisal.updateBy}


    and pa.update_time=#{performanceAppraisal.updateTime}

ORDER BY pa.appraisal_year,pa.cycle_number

SELECT pao.perform_appraisal_objects_id, pao.performance_appraisal_id, pao.appraisal_object_id, pao.appraisal_principal_id, IFNULL( pao.evaluation_score, 0.00 ) AS evaluation_score, pao.appraisal_result_id, pao.sort, pao.appraisal_object_status, pao.appraisal_principal_name, pao.create_by, pao.create_time, pao.update_by, pao.update_time, pas.appraisal_object_name, pas.appraisal_object_code, pas.department_id, pas.department_name, pas.post_id, pas.post_name, pas.official_rank_system_id, pas.official_rank, pas.official_rank_name, pa.appraisal_name, pa.appraisal_year, pa.cycle_type, pa.cycle_number, pa.appraisal_status, pas.appraisal_object_name, pao.appraisal_principal_name FROM performance_appraisal_objects pao LEFT JOIN perform_appraisal_object_snap pas ON pao.perform_appraisal_objects_id = pas.perform_appraisal_objects_id AND pas.delete_flag = 0 LEFT JOIN performance_appraisal pa ON pa.performance_appraisal_id = pao.performance_appraisal_id AND pa.delete_flag = 0 WHERE pao.delete_flag = 0
and pa.appraisal_name like concat('%', #{performanceAppraisalObjectsDTO.appraisalName}, '%')
and pa.appraisal_year=#{performanceAppraisalObjectsDTO.appraisalYear}
and pa.cycle_type=#{performanceAppraisalObjectsDTO.cycleType}
and pa.cycle_number=#{performanceAppraisalObjectsDTO.cycleNumber}
and pas.department_name like concat('%', #{performanceAppraisalObjectsDTO.departmentName}, '%')
and pao.appraisal_principal_name like concat('%', #{performanceAppraisalObjectsDTO.appraisalPrincipalName}, '%')
AND pao.appraisal_object_status IN
#{item}
AND pao.performance_appraisal_id IN
#{item}
ORDER BY pao.evaluation_score, pao.sort

mapper.delete:
DELETE FROM performance_appraisal
WHERE performance_appraisal_id IN

    #{item}

