mapper.select:
SELECT product_id, parent_product_id, ancestors, product_code, product_name, level, product_unit_id, product_category, product_description, list_price, listing_flag, delete_flag, create_by, create_time, update_by, update_time FROM product WHERE product_unit_id in
#{item}
and delete_flag=0

SELECT
    product_id, parent_product_id, ancestors, product_code, product_name, level, product_unit_id, product_category, product_description, list_price, listing_flag, delete_flag, create_by, create_time, update_by, update_time
FROM product
WHERE  product_description=#{product.productCategory}
  or listing_flag = #{product.listingFlag}
  and delete_flag=0

mapper.update:
UPDATE product
SET     delete_flag= 1,
        update_by=#{updateBy},
        update_time=#{updateTime}
WHERE
        product_id=#{product.productId}

mapper.delete:
