mapper.select:
SELECT target_outcome_id, target_year, delete_flag, create_by, create_time, update_by, update_time FROM target_outcome WHERE target_outcome_id in
#{item}
and delete_flag=0

SELECT
target_outcome_id, target_year, delete_flag, create_by, create_time, update_by, update_time
FROM target_outcome
WHERE delete_flag=0

    and target_outcome_id=#{targetOutcome.targetOutcomeId}


    and target_year=#{targetOutcome.targetYear}


    and update_by=#{targetOutcome.updateBy}


    and DATE_FORMAT( create_time, '%Y%m%d' ) = DATE_FORMAT( #{targetOutcome.createTime}, '%Y%m%d' )


    and create_time > #{targetOutcome.params.createTimeStart}
    and create_time < #{targetOutcome.params.createTimeEnd}

ORDER BY target_year ASC

SELECT to2.target_year, tod.indicator_id, IFNULL(tod.actual_january,0) as actual_january, IFNULL(tod.actual_february,0) as actual_february, IFNULL(tod.actual_march,0) as actual_march, IFNULL(tod.actual_april,0) as actual_april, IFNULL(tod.actual_may,0) as actual_may, IFNULL(tod.actual_june,0) as actual_june, IFNULL(tod.actual_july,0) as actual_july, IFNULL(tod.actual_august,0) as actual_august, IFNULL(tod.actual_september,0) as actual_september, IFNULL(tod.actual_october,0) as actual_october, IFNULL(tod.actual_november,0) as actual_november, IFNULL(tod.actual_december,0) as actual_december from target_outcome to2 left join target_outcome_details tod on to2.target_outcome_id = tod.target_outcome_id and tod.delete_flag =0 WHERE to2.delete_flag = 0 and tod.indicator_id in
#{item}
and to2.target_year <= #{targetOutcome.targetYear} order by to2.target_year desc LIMIT #{targetOutcome.limitYear}

SELECT tao.target_outcome_id,
tao.target_year,
tao.delete_flag,
tao.create_by,
tao.create_time,
tao.update_by,
tao.update_time
FROM target_outcome tao
WHERE tao.delete_flag = 0

    and tao.target_outcome_id=#{targetOutcome.targetOutcomeId}


    and tao.target_year=#{targetOutcome.targetYear}


    and tao.update_by=#{targetOutcome.updateBy}


    and tao.update_time=#{targetOutcome.updateTime}


    and tao.create_time > #{targetOutcome.params.createTimeStart}
    and tao.create_time < #{targetOutcome.params.createTimeEnd}


    AND tao.create_by IN

        #{item}


ORDER BY tao.target_year ASC

select to2.target_year , tod.target_outcome_details_id, tod.target_outcome_id, tod.indicator_id, tod.actual_total, tod.actual_january, tod.actual_february, tod.actual_march, tod.actual_april, tod.actual_may, tod.actual_june, tod.actual_july, tod.actual_august, tod.actual_september, tod.actual_october, tod.actual_november, tod.actual_december, tod.delete_flag, tod.create_by, tod.create_time, tod.update_by, tod.update_time, tod.tenant_id from target_outcome_details tod left join target_outcome to2 on to2.target_outcome_id = tod.target_outcome_id and to2.delete_flag = 0 where tod.delete_flag = 0 and indicator_id in
#{item}
and to2.target_year in
#{item}

mapper.update:
UPDATE target_outcome
SET delete_flag= 1,
update_by=#{targetOutcome.updateBy},
update_time=#{targetOutcome.updateTime}
WHERE target_outcome_id = #{targetOutcome.targetOutcomeId}

mapper.delete:
DELETE FROM target_outcome
WHERE target_outcome_id IN

    #{item}

mapper.update:
update target_outcome




                when target_outcome_id=#{item.targetOutcomeId} then #{item.targetYear}






                when target_outcome_id=#{item.targetOutcomeId} then #{item.deleteFlag}






                when target_outcome_id=#{item.targetOutcomeId} then #{item.createBy}






                when target_outcome_id=#{item.targetOutcomeId} then #{item.createTime}






                when target_outcome_id=#{item.targetOutcomeId} then #{item.updateBy}






                when target_outcome_id=#{item.targetOutcomeId} then #{item.updateTime}




where

    target_outcome_id=#{item.targetOutcomeId}
