and delete_flag=0

SELECT
target_setting_id, target_setting_type, indicator_id, target_year, percentage,
challenge_value,target_value, guaranteed_value,sort, delete_flag, create_by, create_time, update_by, update_time
FROM target_setting
WHERE delete_flag=0

    and target_setting_id=#{targetSetting.targetSettingId}


    and target_setting_type=#{targetSetting.targetSettingType}


    and indicator_id=#{targetSetting.indicatorId}


    and target_year=#{targetSetting.targetYear}


    and percentage=#{targetSetting.percentage}


    and challenge_value=#{targetSetting.challengeValue}


    and target_value=#{targetSetting.targetValue}


    and guaranteed_value=#{targetSetting.guaranteedValue}


    and sort=#{targetSetting.sort}


    and delete_flag=#{targetSetting.deleteFlag}


    and create_by=#{targetSetting.createBy}


    and create_time=#{targetSetting.createTime}


    and update_by=#{targetSetting.updateBy}


    and update_time=#{targetSetting.updateTime}

ORDER BY sort ASC

SELECT sum(tod.actual_august) as actual_august, sum(tod2.actual_august) as last_actual_august, ts.target_setting_id, ts.target_setting_type, ts.indicator_id, '订单（不含税）' as indicator_name, ts.target_year, ts.percentage, sum(ts.challenge_value) as challenge_value, sum(ts.target_value) as target_value, sum(ts.guaranteed_value) as guaranteed_value, ts.sort, ts.delete_flag, ts.create_by, ts.create_time, ts.update_by, ts.update_time FROM target_setting ts left join target_outcome toe on toe.target_year = ts.target_year and toe.delete_flag = 0 left join target_outcome_details tod on ts.indicator_id = tod.indicator_id and tod.delete_flag = 0 left join target_outcome_details tod2 on (select target_outcome_id from target_outcome where target_year=#{targetSetting.targetYear}-1 and delete_flag = 0) = tod2.target_outcome_id and tod2.delete_flag = 0 WHERE ts.delete_flag = 0 and ts.indicator_id = #{targetSetting.indicatorIds[0]}
and ts.target_year=#{targetSetting.targetYear}
