mapper.select:
SELECT
department_id, parent_department_id, ancestors, department_code, department_name, level, department_leader_id,
department_leader_post_id, examination_leader_id, department_importance_factor, department_description, sort,
status, delete_flag, create_by, create_time, update_by, update_time
FROM department
WHERE delete_flag=0

    and department_id=#{department.departmentId}


    and parent_department_id=#{department.parentDepartmentId}


    and ancestors=#{department.ancestors}


    and department_code=#{department.departmentCode}


    and (
    department_code=#{item}
)


    and department_name=#{department.departmentName}


    and level=#{department.level}


    and department_leader_id=#{department.departmentLeaderId}


    and department_leader_post_id=#{department.departmentLeaderPostId}


    and examination_leader_id=#{department.examinationLeaderId}


    and department_importance_factor=#{department.departmentImportanceFactor}


    and department_description=#{department.departmentDescription}


    and sort=#{department.sort}


    and status=#{department.status}


    and delete_flag=#{department.deleteFlag}


    and create_by=#{department.createBy}


    and create_time=#{department.createTime}


    and update_by=#{department.updateBy}


    and update_time=#{department.updateTime}

SELECT department_id, parent_department_id, ancestors, department_code, department_name, `level`, department_leader_id, department_leader_post_id, examination_leader_id, department_importance_factor, department_description, sort, status, delete_flag, create_by, create_time, update_by, update_time FROM department where
department_code=#{departmentCode}
and delete_flag = 0

SELECT department_id, parent_department_id, ancestors, department_code, department_name, `level`, department_leader_id, department_leader_post_id, examination_leader_id, department_importance_factor, department_description, sort, status, delete_flag, create_by, create_time, update_by, update_time FROM department where department_code in
#{item}
and delete_flag = 0

SELECT d.department_id, d.parent_department_id, d.ancestors, d.department_code, d.department_name, d.`level`, d.department_leader_id, d.department_leader_post_id, d.examination_leader_id, d.department_importance_factor, d.department_description, d.sort, d.status, d.delete_flag, d.create_by, d.create_time, d.update_by, d.update_time, e.employee_name as department_leader_name, ee.employee_name as examination_leader_name FROM department d LEFT JOIN employee e ON d.department_leader_id = e.employee_id AND e.delete_flag =0 LEFT JOIN employee ee ON d.examination_leader_id = ee.employee_id AND d.delete_flag =0 where department_id in
#{item}
and d.delete_flag = 0

mapper.update:
UPDATE department
SET delete_flag= 1,
    update_by=#{department.updateBy},
    update_time=#{department.updateTime}
WHERE department_id = #{department.departmentId}

mapper.delete:
DELETE FROM department
WHERE department_id IN

    #{item}

