SELECT post_id, post_code, post_name, official_rank_system_id, post_rank_lower, post_rank, post_rank_upper, post_description, status, delete_flag, create_by, create_time, update_by, update_time FROM post WHERE official_rank_system_id IN
#{item}
and delete_flag = 0

mapper.update:
UPDATE post
SET delete_flag= 1,
    update_by=#{post.updateBy},
    update_time=#{post.updateTime}
WHERE post_id = #{post.postId}

