mapper.select:
SELECT
menu_id, parent_menu_id, menu_type, menu_name, product_package_id, sort, path, component, query, external_link_flag, cache_flag, visible_flag, permission_code, icon, status, delete_flag, create_by, create_time, update_by, update_time
FROM menu
WHERE delete_flag=0

            and menu_id=#{menu.menuId}


            and parent_menu_id=#{menu.parentMenuId}


            and menu_type=#{menu.menuType}


            and menu_name=#{menu.menuName}


            and product_package_id=#{menu.productPackageId}


            and sort=#{menu.sort}


            and path=#{menu.path}


            and component=#{menu.component}


            and query=#{menu.query}


            and external_link_flag=#{menu.externalLinkFlag}


            and cache_flag=#{menu.cacheFlag}


            and visible_flag=#{menu.visibleFlag}


            and permission_code=#{menu.permissionCode}


            and icon=#{menu.icon}


            and status=#{menu.status}


            and delete_flag=#{menu.deleteFlag}


            and create_by=#{menu.createBy}


            and create_time=#{menu.createTime}


            and update_by=#{menu.updateBy}


            and update_time=#{menu.updateTime}

ORDER BY parent_menu_id, sort

SELECT m.menu_id, m.parent_menu_id, m.menu_type, m.menu_name, m.product_package_id, m.sort, m.path, m.component, m.query, m.external_link_flag, m.cache_flag, m.visible_flag, m.permission_code, m.icon, m.status, m.delete_flag, m.create_by, m.create_time, m.update_by, m.update_time FROM menu m LEFT JOIN role_menu rm on m.menu_id = rm.menu_id AND rm.delete_flag = 0 LEFT JOIN user_role ur on rm.role_id = ur.role_id AND ur.delete_flag = 0 LEFT JOIN role ro on ur.role_id = ro.role_id WHERE m.delete_flag = 0 AND ur.user_id = #{params.userId}
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.visible_flag = #{visibleFlag}
AND m.status = #{status}
ORDER BY m.parent_menu_id, m.sort

mapper.update:
UPDATE menu
SET     delete_flag= 1,
        update_by=#{menu.updateBy},
        update_time=#{menu.updateTime}
WHERE
        menu_id=#{menu.menuId}

mapper.delete:
