mapper.update:
UPDATE tenant
SET delete_flag= 1,
    update_by=#{tenant.updateBy},
    update_time=#{tenant.updateTime}
WHERE tenant_id = #{tenant.tenantId}

mapper.delete:
DELETE FROM tenant
WHERE tenant_id IN

    #{item}

