mapper.select:
SELECT
user_id, employee_id, user_account, mobile_phone, email
FROM user
WHERE delete_flag=0

    and employee_id=#{user.employeeId}


    and user_account=#{user.userAccount}


    and mobile_phone=#{user.mobilePhone}


    and email=#{user.email}

SELECT distinct u.user_id, u.employee_id, u.user_account, u.password, u.user_name, u.mobile_phone, u.email, u.avatar, u.remark, u.status, u.delete_flag, u.create_by, u.create_time, u.update_by, u.update_time,e.employee_name FROM user u LEFT JOIN employee e ON e.employee_id=u.employee_id AND e.delete_flag = 0 LEFT JOIN user_role ur on ur.user_id = u.user_id AND ur.delete_flag = 0 LEFT JOIN role r on ur.role_id = r.role_id AND r.delete_flag = 0 WHERE u.delete_flag = 0 AND r.role_id = #{user.params.roleId}
and u.user_account LIKE concat('%', #{userAccount}, '%')
and u.mobile_phone LIKE concat('%', #{mobilePhone}, '%')
and u.email=#{user.email}
and u.status=#{user.status}
AND e.employee_name LIKE concat('%', #{user.params.employeeName}, '%')

SELECT u.user_id, u.employee_id, u.user_account, u.password, u.user_name, u.mobile_phone, u.email, u.avatar, u.remark, u.status, u.delete_flag, u.create_by, u.create_time, u.update_by, u.update_time,e.employee_name FROM user u LEFT JOIN employee e ON e.employee_id=u.employee_id AND e.delete_flag = 0 WHERE u.delete_flag=0
and u.user_id=#{user.userId}
and u.user_account LIKE concat('%', #{userAccount}, '%')
and u.mobile_phone LIKE concat('%', #{mobilePhone}, '%')
and u.email=#{user.email}
and u.status=#{user.status}
AND e.employee_name LIKE concat('%', #{user.params.employeeName}, '%')
AND date_format(u.create_time,'%y%m%d') >= date_format(#{user.params.createTimeStart},'%y%m%d')
AND date_format(u.create_time,'%y%m%d') <= date_format(#{user.params.createTimeEnd},'%y%m%d')

mapper.update:
UPDATE user
SET delete_flag= 1,
update_by=#{user.updateBy},
update_time=#{user.updateTime}
WHERE
user_id=#{user.userId}

mapper.delete:
DELETE FROM user
WHERE user_id IN

    #{item}

mapper.update:
update user




                when user_id=#{item.userId} then #{item.employeeId}






                when user_id=#{item.userId} then #{item.userAccount}






                when user_id=#{item.userId} then #{item.password}






                when user_id=#{item.userId} then #{item.userName}






                when user_id=#{item.userId} then #{item.mobilePhone}






                when user_id=#{item.userId} then #{item.email}






                when user_id=#{item.userId} then #{item.avatar}






                when user_id=#{item.userId} then #{item.remark}






                when user_id=#{item.userId} then #{item.status}






                when user_id=#{item.userId} then #{item.deleteFlag}






                when user_id=#{item.userId} then #{item.createBy}






                when user_id=#{item.userId} then #{item.createTime}






                when user_id=#{item.userId} then #{item.updateBy}






                when user_id=#{item.userId} then #{item.updateTime}




where

    user_id=#{item.userId}
